package learningjava;

public class swtichCase {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int number =10;
		
		switch(number) {
		
		case 1:
			System.out.println("Monday");
			break;
		case 2:
			System.out.println("Tuesday");
			break;
		default:
			System.out.println("default");
		
		}
		

	}

}
